import React, { useState, useEffect } from 'react';
import axios from 'axios';
import './ImageUpload.css';

function ImageUpload() {
    const [file, setFile] = useState(null);
    const [results, setResults] = useState([]);
    const [loading, setLoading] = useState(false);
    // const [diagnosisHistory, setDiagnosisHistory] = useState([]);

    // Fetch diagnosis history when component mounts
    // useEffect(() => {
    //     const fetchDiagnosisHistory = async () => {
    //         try {
    //             const token = localStorage.getItem('token');
    //             const response = await axios.get('http://localhost:5000/diagnosis-history', {
    //                 headers: { 
    //                     'Authorization': `Bearer ${token}` 
    //                 }
    // //             });
    //             setDiagnosisHistory(response.data);
    //         } catch (error) {
    //             console.error('Error fetching diagnosis history:', error);
    //         }
    //     };

    //     fetchDiagnosisHistory();
    // }, []);

    const handleFileChange = (e) => {
        setFile(e.target.files[0]);
    };

    const handleSubmit = async (e) => {
        e.preventDefault();
        if (!file) return;

        const formData = new FormData();
        formData.append('image', file);

        setLoading(true);
        try {
            const token = localStorage.getItem('token');
            const response = await axios.post('http://localhost:5000/api/upload', formData, {
                headers: { 
                    'Content-Type': 'multipart/form-data',
                    'Authorization': `Bearer ${token}`
                },
            });

            // Sort the results by confidence scores in ascending order
            const sortedResults = response.data.diagnosis;
            setResults(sortedResults);

            // Refetch diagnosis history to include the new diagnosis
            // const historyResponse = await axios.get('http://localhost:5000/diagnosis-history', {
            //     headers: { 
            //         'Authorization': `Bearer ${token}` 
            //     }
            // });
            // setDiagnosisHistory(historyResponse.data);
        } catch (error) {
            console.error('Error:', error);
            setResults([{ label: 'Error processing image', score: 0 }]);
        }
        setLoading(false);
    };

    return (
        <div className="upload-container">
            <h2>Plant Disease Detection</h2>
            <form onSubmit={handleSubmit}>
                <input type="file" onChange={handleFileChange} />
                <button type="submit" disabled={loading}>Upload</button>
            </form>
            {loading && <p>Loading...</p>}
            <div className="results-container">
                {results.map((result, index) => (
                    <div key={index} className="result-box">
                        <h3>{result.label}</h3>
                        <p>Confidence: {(result.score * 100).toFixed(2)}%</p>                       
                    </div>
                ))}
            </div>

            {/* <div className="diagnosis-history">
                <h3>Diagnosis History</h3>
                {diagnosisHistory.map((record, index) => (
                    <div key={index} className="history-item">
                        <img 
                            src={`http://localhost:5000/${record.imagePath}`} 
                            alt={`Diagnosis ${index + 1}`} 
                            style={{ maxWidth: '200px', maxHeight: '200px' }} 
                        />
                        <div className="history-details">
                            {record.diagnosis.map((result, diagnosisIndex) => (
                                <div key={diagnosisIndex}>
                                    <p>{result.label}: {(result.score * 100).toFixed(2)}%</p>
                                </div>
                            ))}
                            <p>Date: {new Date(record.createdAt).toLocaleString()}</p>
                        </div>
                    </div>
                ))}
            </div> */}
        </div>
    );
}

export default ImageUpload;
