import React, { useState } from "react";
import axios from "axios";
import "./ImageUpload.css";

function ImageUpload() {
    const [file, setFile] = useState(null);
    const [results, setResults] = useState([]);
    const [loading, setLoading] = useState(false);
    const [symptomsAndRemedies, setSymptomsAndRemedies] = useState(null);

    // Handle file change
    const handleFileChange = (e) => {
        setFile(e.target.files[0]);
    };

    // Submit the file to the backend
    const handleSubmit = async (e) => {
        e.preventDefault();
        if (!file) return;

        const formData = new FormData();
        formData.append("image", file);

        setLoading(true);
        try {
            const response = await axios.post("http://localhost:5000/api/upload", formData, {
                headers: { "Content-Type": "multipart/form-data" },
            });
            setResults(response.data.diagnosis); // Assuming diagnosis contains detected diseases
        } catch (error) {
            console.error("Error:", error);
            setResults([]);
        }
        setLoading(false);
    };

    // Fetch symptoms and remedies
    const handleSymptomsAndRemedies = async (diseaseName) => {
        try {
            const response = await axios.post("http://localhost:5000/api/symptoms-remedies", { disease: diseaseName });
            setSymptomsAndRemedies(response.data); // Response contains symptoms and remedies
        } catch (error) {
            console.error("Error fetching symptoms and remedies:", error);
            setSymptomsAndRemedies({ error: "Unable to fetch symptoms and remedies" });
        }
    };

    return (
        <div className="page-container">
            {/* Left Section: Upload and Disease Names */}
            <div className="left-half">
                <h2>Plant Disease Detection</h2>
                <form onSubmit={handleSubmit}>
                    <input type="file" onChange={handleFileChange} />
                    <button type="submit" disabled={loading}>
                        {loading ? "Uploading..." : "Upload"}
                    </button>
                </form>
                <div className="results-container">
                    {results.map((result, index) => (
                        <div key={index} className="result-box">
                            <h3>{result.label}</h3>
                            <p>Confidence: {(result.score * 100).toFixed(2)}%</p>
                            <button onClick={() => handleSymptomsAndRemedies(result.label)}>
                                Show Symptoms and Remedies
                            </button>
                        </div>
                    ))}
                </div>
            </div>

            {/* Right Section: Symptoms and Remedies */}
            <div className="right-half">
                <h2>Symptoms and Remedies</h2>
                {symptomsAndRemedies ? (
                    symptomsAndRemedies.error ? (
                        <p>{symptomsAndRemedies.error}</p>
                    ) : (
                        <div className="symptoms-remedies">
                            <h3>{symptomsAndRemedies.disease}</h3>
                            <p>
                                <strong>Symptoms:</strong> {symptomsAndRemedies.symptoms}
                            </p>
                            <p>
                                <strong>Remedies:</strong> {symptomsAndRemedies.remedies}
                            </p>
                        </div>
                    )
                ) : (
                    <p>Select a disease to view symptoms and remedies.</p>
                )}
            </div>
        </div>
    );
}

export default ImageUpload;
